<?php

    $morse = array(" " => "/ ", "a" => ".- ", "b" => "-... ", "c" => "-.-. ", "d" => "-.. ", "e" => ". ", "f" => "..-. ", "g" => "--. ", "h" => ".... ", "i" => ".. ", "j" => ".--- ", "k" => "-.- ", "l" => ".-.. ", "m" => "-- ", "n" => "-. ", "o" => "--- ", "p" => ".--. ", "q" => "--.- ", "r" => ".-. ", "s" => "... ", "t" => "- ", "u" => "..- ", "v" => "...- ", "w" => ".-- ", "x" => "-..- ", "y" => "-.-- ", "z" => "--.. ", "." => ".-.-.- ", "," => "--..-- ", "?" => "..--.. ", "/" => "-..-. ", "@" => ".--.-. ", "1" => ".---- ", "2" => "..--- ", "3" => "...-- ", "4" => "....- ", "5" => "..... ", "6" => "-.... ", "7" => "--... ", "8" => "---.. ", "9" => "----. ", "0" => "----- ", ":" => "---... ", "'" => ".----. ", "-" => "-....- ", '"' => ".-..-. ", "=" => "-...- ", "(" => "-.--.- ");

    function translateText($text){
        return trim(join(array_map(function($e){global $morse;return $morse[$e];},str_split(strtolower($text)))));
    }

    function translateMorse($text){
        return join(array_map(function($e){global $morse;return array_flip(array_map('trim',$morse))[$e];},explode(' ',$text)));
    }